        # 进位标识
        carry = 1

        for i in range(len(digits) - 1, -1, -1):

            s = digits[i] + carry
            digits[i] = s % 10
            # 注意：整除要使用 //
            carry = s // 10

            if carry == 0:
                return digits
        print(digits, carry)

        if carry == 1:
            return [1] + digits


