public class Solution {

    // 从左下角开始，尝试不断向右边走
    // 右边走不动了，就向下面走，直到出边界

    public boolean searchMatrix(int[][] matrix, int target) {
        int row = matrix.length;
        if (row == 0) {
            return false;
        }
        int col = matrix[0].length;
        // 从左下角开始搜索
        int x = row - 1;
        int y = 0;
        // 每次考虑向上走
        while (x >= 0) {
            // 向上走之前，尽量向右边走
            while (y < col && matrix[x][y] < target) {
                y++;
            }
            if (y < col && matrix[x][y] == target) {
                return true;
            }
            x--;
        }
        return false;
    }
}

