public class Solution2 {

    public boolean hasAlternatingBits(int n) {
        String nStr = Integer.toBinaryString(n);
        int len = nStr.length();
        for (int i = 0; i < len - 1; i++) {
            if (nStr.charAt(i) == nStr.charAt(i + 1)) {
                return false;
            }
        }
        return true;
    }
}





